from tkinter import *import randomimport timeroot = Tk() # Создаем графический интерфейс для игрыroot.geometry("500x500") # задаем размер окна 440x170root.title("Программа запуска ракет") # задаем заголовок окнуroot.resizable(False, False) # запрещаем изменение размеров окнаroot["bg"] = "#FFF" # делаем фон окна белымdef test():    xb = random.randint(0,400)    yb = random.randint(0,450)    if (xb > 100 and xb < 200) and (yb > 50 and yb < 200):        root.title("Ракета \"на Америку\" запущена")        time.sleep(4)        root.destroy()    else:        btn1.place(x = xb,y = yb)btn1 = Button(root, text="Красная \nкнопка", font=("Times New Roman", 13), command=test)btn1.place(x=200, y=200, width=100, height=50)root.mainloop()