test = ["lop","lop","lop","ypi","lop","lop"]y = 0sovpadenia = 0ru = ""ps = []po = ""for i in test:    y += 1    if y == 1:        ru = i    if i == ru:        sovpadenia += 1    if i != ru:        ps.append(i)        po = i        if len(ps) > sovpadenia:    print("Лишний элемент",ru)    test.count(ru)    for x in range(test.count(ru)):        test.remove(ru)    print(test)else:    print("Лишний элемент",po)    test.count(po)    for x in range(test.count(po)):        test.remove(po)    print(test)input()